<style lang="scss">
.nav {
  width: 250px;
  display: flex;
  flex-direction: column;
  padding: 105px 30px 0 40px;

  &__link {
    display: flex;
    align-items: center;
    justify-content: flex-start;

    &.active {
      span {
        color: #fff;
        transition: all .3s;
      }

      svg path {
        fill: #6781F7;
        transition: all .3s;
      }

      &::before {
        opacity: 1;
      }
    }

    &-text {
      color: #8B97AE;
      margin-left: 20px;
      transition: all .3s;
    }

    svg path {
      fill: #8B97AE;
      transition: all .3s;
    }
    
    &:not(:last-child) {
      margin-bottom: 28px;
    }

    &:hover,
    &:focus {
      span {
        color: #fff;
        transition: all .3s;
      }

      svg path {
        fill: #6781F7;
        transition: all .3s;
      }

      &::before {
        transition: all .3s;
        opacity: 1;
      }
    }

    &::before {
      content: '';
      position: absolute;
      display: block;
      width: 4px;
      height: 30px;
      background: #6781F7;
      left: 10px;
      transition: all .3s;
      opacity: 0;
    }
  }
}

@media screen and (max-width: 780px) {
  .nav {
    display: none;
    
    &.active {
      display: block;
      position: absolute;
      z-index: 5;
      padding: 15px;
      background: #131b29;
      border-radius: 5px;
      right: 0;
      top: 65px;
    }

    &__link::before {
      display: none;
    }
  }
}
</style>